//msg.dev_details = global.get( msg.sensor_mac + "_dev")
msg.details = {
    "sensor_devvar" : msg.sensor_mac + "_dev" , 
    "sensor_name" : msg.sensor_name ,
    "sensor_mac" : msg.sensor_mac ,
    "sensor_mac_clean" : msg.sensor_mac_clean ,
    "sensor_dev_details" : msg.sensor_dev_details
}
return msg;

var device = global.get(msg.sensor_mac);
msg.sensor_name = device.name;
msg.device = device
return msg;

msg.topic = msg.discovery_binary_sensor + msg.sensor_mac_clean + "/" + msg.sensor_type + "/config" 
msg.payload.state_topic = msg.old_topic
return msg;

msg.url = "https://api.meraki.com/api/v1/organizations/" + msg.org_id +"/devices";
msg.headers = {};
msg.headers['content-type'] = 'application/json';
msg.headers['X-Cisco-Meraki-API-Key'] = msg.secret ;
return msg;

for (let i = 0 ; i < msg.payload.length; i++) {
  var device = {};
  device.identifiers = [ msg.payload[i].serial ];
  device.name = msg.payload[i].name ;
//  device.mac = msg.payload[i].mac ;
  device.model = msg.payload[i].model ;
  device.sw_version = msg.payload[i].firmware ;
  device.configuration_url = msg.payload[i].url ;
  device.manufacturer = "Cisco Meraki" ;
//  device.latitude = msg.payload[i].lat ;
//  device.longitude = msg.payload[i].lng ;
  devmac = msg.payload[i].mac ; 
  global.set(devmac , device) ;

}
return msg;

msg.topic = msg.discovery_sensor + msg.sensor_mac_clean + "/" + msg.sensor_type + "/config" 
msg.payload.state_topic = msg.old_topic
return msg;
